from langflow.custom import Component
from langflow.io import MessageInput, Output
from langflow.schema import Message

class SeparateTextAndFile(Component):
    display_name = "Separate Text and File"
    description = "Separates text and file from ChatInput"

    inputs = [
        MessageInput(name="chat_input", display_name="Chat Input"),
    ]

    outputs = [
        Output(display_name="File Message", name="file", method="get_file"),
        Output(display_name="Text Message", name="text", method="get_text")
    ]

    def get_text(self) -> Message:
        text_message = Message(text=self.chat_input.text)
        return text_message

    def get_file(self) -> Message:
        file_message = Message(files=self.chat_input.files)
        if self.chat_input.files:
            return file_message
        return Message()
